import{_ as e,e as o}from"./app.625aa393.js";var d="/assets/01.ff2dac8c.png",c="/assets/02.501e0ab1.png",a="/assets/03.bdb27adf.png",i="/assets/04.90222253.png",r="/assets/05.3bd50059.png",p="/assets/06.7c48bde9.png",l="/assets/07.38ab752c.png",t="/assets/08.411dfff2.png",s="/assets/09.e50b5c94.png",n="/assets/10.55d0533f.png",h="/assets/11.5089dd79.png",g="/assets/12.dc8cb7b2.png",u="/assets/13.6a43037d.png",m="/assets/14.977ec18c.png",y="/assets/15.8f1f0efb.png";const b={},_=o('<h2 id="cpu-\u548C-gpu" tabindex="-1"><a class="header-anchor" href="#cpu-\u548C-gpu" aria-hidden="true">#</a> CPU \u548C GPU</h2><p><img src="'+d+'" alt="" loading="lazy"></p><p>\u56FE\u5F62\u7ED8\u5236\u9700\u8981\u8FDB\u884C\u5927\u91CF\u7684\u7B97\u672F\u903B\u8F91\u8FD0\u7B97\u3002\u5728\u6CA1\u6709 <code>GPU</code> \u4E4B\u524D\uFF0C\u8BA1\u7B97\u673A\u7684\u7B97\u672F\u903B\u8F91\u8FD0\u7B97\u90FD\u662F\u4EA4\u7531 <code>CPU</code> \u4E2D\u7684 <code>ALU</code> \u7B97\u672F\u903B\u8F91\u5355\u5143\u5B8C\u6210\u7684\u3002 \u4F46\u662F\uFF0C<code>CPU</code> \u4F5C\u4E3A\u4E2D\u592E\u5904\u7406\u5668\uFF0C\u7EDF\u7BA1\u6240\u6709\u7A0B\u5E8F\uFF0C\u5176\u4E2D\u6709\u9650\u7684 <code>ALU</code> \u4E0D\u80FD\u6EE1\u8DB3\u56FE\u5F62\u7ED8\u5236\u65F6\u6240\u9700\u7684\u5927\u91CF\u7B97\u672F\u903B\u8F91\u8BA1\u7B97\u7684\u8981\u6C42\uFF0C\u6240\u4EE5\u56FE\u5F62\u8D28\u91CF\u96BE\u4EE5\u63D0\u5347\u3002</p><p>\u800C <code>GPU</code> \u5219\u662F\u4E13\u95E8\u7528\u6765\u8FDB\u884C\u56FE\u5F62\u7ED8\u5236\u65F6\u7684 \u7B97\u672F\u903B\u8F91\u8FD0\u7B97\u7684\uFF0C<code>GPU</code> \u4E2D\u5305\u542B\u4E86\u5F88\u591A\u7684 <code>ALU</code> \u7B97\u672F\u903B\u8F91\u5355\u5143\uFF0C\u53EF\u4EE5\u6EE1\u8DB3\u56FE\u5F62\u7ED8\u5236\u65F6\u6240\u9700\u7684\u5927\u91CF\u7B97\u672F\u903B\u8F91\u8BA1\u7B97\u3002\u56E0\u6B64\uFF0C\u81EA\u4ECE <code>GPU</code> \u51FA\u73B0\u540E\uFF0C<code>CPU</code> \u53EA\u9700\u7ECF\u8FC7\u4E00\u4E9B\u7B80\u5355\u8BA1\u7B97\uFF0C\u628A\u5E03\u5C40\u4FE1\u606F\u8F6C\u6210\u77E2\u91CF\u56FE\u5F62\u4FE1\u606F\uFF0C\u7136\u540E\u628A\u77E2\u91CF\u56FE\u5F62\u4FE1\u606F\u4EA4\u7ED9 <code>GPU</code>\uFF0C\u7531 <code>GPU</code> \u6839\u636E\u77E2\u91CF\u56FE\u5F62\u4FE1\u606F\u8FDB\u884C\u5927\u91CF\u7684\u7B97\u672F\u903B\u8F91\u8BA1\u7B97\uFF0C\u7ECF\u8FC7\u6805\u683C\u5316\u4E4B\u540E\uFF0C\u628A\u77E2\u91CF\u56FE\u5F62\u4FE1\u606F\u8F6C\u6210 <code>Bitmap</code> \u4F4D\u56FE\u653E\u5230\u663E\u793A\u5668\u4E0A\u663E\u793A\u3002</p><p><img src="'+c+'" alt="" loading="lazy"></p><h2 id="\u5237\u65B0\u9891\u7387\u548C-vsync-\u4FE1\u53F7" tabindex="-1"><a class="header-anchor" href="#\u5237\u65B0\u9891\u7387\u548C-vsync-\u4FE1\u53F7" aria-hidden="true">#</a> \u5237\u65B0\u9891\u7387\u548C <code>VSync</code> \u4FE1\u53F7</h2><h3 id="_60hz-\u5237\u65B0\u9891\u7387\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#_60hz-\u5237\u65B0\u9891\u7387\u7684\u7531\u6765" aria-hidden="true">#</a> <code>60Hz</code> \u5237\u65B0\u9891\u7387\u7684\u7531\u6765</h3><p><strong><code>12fps</code></strong>\uFF1A\u7531\u4E8E\u4EBA\u7C7B\u773C\u775B\u7684\u7279\u6B8A\u751F\u7406\u7ED3\u6784\uFF0C\u5982\u679C\u6240\u770B\u753B\u9762\u7684\u5E27\u7387\u9AD8\u4E8E\u6BCF\u79D2\u7EA6 <code>10-12</code> \u5E27\u7684\u65F6\u5019\uFF0C\u5C31\u4F1A\u8BA4\u4E3A\u662F\u8FDE\u8D2F\u7684\uFF08\u89C6\u89C9\u6EDE\u7559\u6548\u5E94\uFF09\u3002</p><p><strong><code>24fps</code></strong>\uFF1A\u6709\u58F0\u7535\u5F71\u7684\u62CD\u6444\u53CA\u64AD\u653E\u5E27\u7387\u5747\u4E3A\u6BCF\u79D2 <code>24</code> \u5E27\uFF0C\u5BF9\u4E00\u822C\u4EBA\u800C\u8A00\u5DF2\u7B97\u53EF\u63A5\u53D7\u3002</p><p><strong><code>30fps</code></strong>\uFF1A\u65E9\u671F\u7684\u9AD8\u52A8\u6001\u7535\u5B50\u6E38\u620F\uFF0C\u5E27\u7387\u5C11\u4E8E\u6BCF\u79D2 <code>30</code> \u5E27\u7684\u8BDD\u5C31\u4F1A\u663E\u5F97\u4E0D\u8FDE\u8D2F\uFF0C\u8FD9\u662F\u56E0\u4E3A\u6CA1\u6709\u52A8\u6001\u6A21\u7CCA\u4F7F\u6D41\u7545\u5EA6\u964D\u4F4E\u3002</p><p><strong><code>60fps</code></strong>\uFF1A\u5728\u4E8E\u624B\u673A\u4EA4\u4E92\u8FC7\u7A0B\u4E2D\uFF0C\u5982\u89E6\u6478\u548C\u53CD\u9988 <code>60</code> \u5E27\u4EE5\u4E0B\u4EBA\u662F\u80FD\u611F\u89C9\u51FA\u6765\u3002<code>60</code> \u5E27\u4EE5\u4E0A\u4E0D\u80FD\u5BDF\u89C9\u53D8\u5316\u3002\u5F53\u5E27\u7387\u4F4E\u4E8E <code>60fps</code> \u65F6\u611F\u89C9\u753B\u9762\u51FA\u73B0\u5361\u987F\u548C\u8FDF\u6EDE\u7684\u73B0\u8C61\u3002</p><p><code>Android</code> \u7CFB\u7EDF\u6BCF\u9694 <code>16ms</code> \u53D1\u51FA <code>VSync</code> \u4FE1\u53F7\uFF08<code>1000ms/60\u224816.66ms</code>\uFF09\uFF0C\u89E6\u53D1\u5BF9 <code>UI</code> \u8FDB\u884C\u6E32\u67D3\uFF0C\u5982\u679C\u6BCF\u6B21\u6E32\u67D3\u90FD\u6210\u529F\uFF0C\u8FD9\u6837\u5C31\u80FD\u8FBE\u5230\u6D41\u7A0B\u7684\u753B\u9762\u6240\u9700\u8981\u7684 <code>60fps</code>\u3002\u4E3A\u4E86\u80FD\u591F\u5B9E\u73B0 <code>60fps</code> \uFF0C\u8FD9\u610F\u5473\u7740\u8BA1\u7B97\u6E32\u67D3\u7684\u5927\u591A\u6570\u64CD\u4F5C\u90FD\u5FC5\u987B\u5728 <code>16ms</code> \u5185\u5B8C\u6210\u3002</p><blockquote><p><code>60fps</code> \u8868\u793A <code>1s</code> \u5185\u8981\u663E\u793A <code>60</code> \u5F20\u56FE\u7247\uFF0C\u4E00\u5F20\u56FE\u7247\u79F0\u4E3A\u4E00\u5E27\u3002</p><p><code>1s</code> \u5185\u663E\u793A <code>60</code> \u5E27\uFF0C\u5219 <code>16.66ms</code> \u5185\u8981\u663E\u793A\u51FA\u4E00\u5E27\u3002\u5426\u5219 <code>1s</code> \u5185\u5C31\u65E0\u6CD5\u663E\u793A\u5B8C <code>60</code> \u5E27\u56FE\u7247\uFF0C\u9020\u6210\u5361\u987F\u3002</p><p><code>VSync</code> \u4FE1\u53F7\u7684\u95F4\u9694\u65F6\u95F4\u8D8A\u77ED\uFF0C<code>1s</code> \u5185\u663E\u793A\u7684\u5E27\u6570\u8D8A\u591A\uFF0C\u753B\u9762\u8D8A\u6D41\u7545\u3002</p></blockquote><h3 id="vsync-\u4FE1\u53F7\u53CA\u5361\u987F\u539F\u7406\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#vsync-\u4FE1\u53F7\u53CA\u5361\u987F\u539F\u7406\u5206\u6790" aria-hidden="true">#</a> <code>VSync</code> \u4FE1\u53F7\u53CA\u5361\u987F\u539F\u7406\u5206\u6790</h3><p><code>VSync</code> \u4FE1\u53F7\u5373\u5782\u76F4\u540C\u6B65\u4FE1\u53F7\u3002</p><p>\u76F8\u90BB\u4E24\u6B21 <code>VSync</code> \u4FE1\u53F7\u7684\u95F4\u9694\u65F6\u95F4\uFF08<code>16ms</code>\uFF09\u5185\u9700\u8981\u5B8C\u6210\u4EE5\u4E0B\u4E24\u5927\u5904\u7406\u8FC7\u7A0B\uFF1A</p><ol><li>\u4ECE <code>CPU</code> \u8BA1\u7B97\u5E03\u5C40\u4FE1\u606F\u5F97\u5230\u77E2\u91CF\u56FE\u5F62\u4FE1\u606F\uFF08\u4E13\u4E1A\u672F\u8BED\uFF1A<strong>\u7EB9\u7406\u5904\u7406</strong>\uFF09\uFF1B</li><li>\u5230 <code>CPU</code> \u5411 <code>GPU</code> \u53D1\u9001\u7EB9\u7406\u4FE1\u606F\uFF0C\u5E76 <code>GPU</code> \u6839\u636E\u7EB9\u7406\u4FE1\u606F\u5BF9\u56FE\u7247\u7684\u6BCF\u4E2A\u50CF\u7D20\u8FDB\u884C\u989C\u8272\u586B\u5145\uFF08\u4E13\u4E1A\u672F\u8BED\uFF1A<strong>\u6805\u683C\u5316\u5904\u7406</strong>\uFF09\u3002</li></ol><p>\u53EA\u6709\u5728\u6B64 <code>16ms</code> \u7684\u95F4\u9694\u65F6\u95F4\u5185\uFF0C\u5B8C\u6210\u4E0A\u9762\u4E24\u4E2A\u6B65\u9AA4\u624D\u80FD\u5C06\u4E00\u5E27\u56FE\u7247\u6210\u529F\u5730\u663E\u793A\u51FA\u6765\u3002\u5426\u5219\u5EF6\u8FDF\u5904\u7406\u5B8C\u6210\u7684\u8FD9\u4E00\u5E27\u56FE\u7247\u65E0\u6CD5\u53CA\u65F6\u663E\u793A\uFF0C\u4ECE\u800C\u5C4F\u5E55\u4E0A\u8FD8\u662F\u663E\u793A\u539F\u6765\u7684\u753B\u9762\uFF08\u5373\u539F\u6765\u7684\u753B\u9762\u5728\u5C4F\u5E55\u4E0A\u591A\u505C\u7559\u4E86 <code>16ms</code>\uFF09\u3002\u5982\u679C\u8FDE\u7EED\u51E0\u6B21\u90FD\u65E0\u6CD5\u5728 <code>16ms</code> \u7684\u95F4\u9694\u65F6\u95F4\u5185\u5B8C\u6210\u4EE5\u4E0A\u4E24\u6B65\uFF0C\u90A3\u4E48\u5C4F\u5E55\u4E0A\u539F\u6765\u7684\u753B\u9762\u5C31\u4F1A\u505C\u7559\u66F4\u957F\u7684\u65F6\u95F4\uFF0C\u4ECE\u800C\u9020\u6210\u5C4F\u5E55\u5361\u987F\u3002</p><p><img src="'+a+'" alt="" loading="lazy"></p><blockquote><p>\u53EA\u6709\u7B2C <code>1</code> \u5E27\u56FE\u7247\u6B63\u5E38\u663E\u793A\uFF0C\u7B2C <code>2</code>\u3001<code>3</code>\u3001<code>4</code> \u5E27\u56FE\u7247\u65E0\u6CD5\u6B63\u5E38\u663E\u793A\uFF0C\u9020\u6210\u5C4F\u5E55\u753B\u9762\u65E0\u6539\u53D8\uFF0C\u4EA7\u751F\u5361\u987F\uFF08<code>Jank</code>\uFF09\u3002</p></blockquote><p>\u5982\u4E0A\u56FE\u6240\u793A\uFF0C\u5F53\u4E00\u5E27\u753B\u9762\u6E32\u67D3\u65F6\u95F4\u8D85\u8FC7 <code>16ms</code> \u7684\u65F6\u5019\uFF0C\u5782\u76F4\u540C\u6B65\u673A\u5236\u4F1A\u8BA9\u663E\u793A\u5668\u786C\u4EF6\u7B49\u5F85 <code>GPU</code> \u5B8C\u6210\u6805\u683C\u5316\u6E32\u67D3\u64CD\u4F5C\uFF0C\u8FD9\u6837\u4F1A\u8BA9\u8FD9\u4E00\u5E27\u753B\u9762\u591A\u505C\u7559\u4E86 <code>16ms</code>\uFF0C\u751A\u81F3\u66F4\u591A\u3002\u8FD9\u6837\u5C31\u9020\u6210\u4E86\u7528\u6237\u770B\u8D77\u6765\u753B\u9762\u505C\u987F\u3002</p><p><img src="'+i+'" alt="" loading="lazy"></p><h3 id="\u5982\u4F55\u907F\u514D\u5361\u987F" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u907F\u514D\u5361\u987F" aria-hidden="true">#</a> \u5982\u4F55\u907F\u514D\u5361\u987F</h3><p>\u5782\u76F4\u540C\u6B65\u4FE1\u53F7\uFF08<code>VSync</code>\u4FE1\u53F7\uFF09\u7684\u95F4\u9694\u65F6\u95F4\uFF08<code>16ms</code>\uFF09\u5185\u4E3B\u8981\u9700\u8981\u5B8C\u6210\u4E24\u4EF6\u4E8B\u60C5\uFF1A</p><ol><li><code>CPU</code> \u5BF9 <code>UI</code> \u5BF9\u8C61\u8FDB\u884C\u5904\u7406\uFF0C\u8F6C\u6362\u4E3A\u4E00\u7CFB\u5217\u7684\u591A\u8FB9\u5F62\uFF08<code>polygons</code>\uFF09\u548C\u7EB9\u7406\uFF08<code>textures</code>\uFF09</li><li><code>CPU</code> \u5C06\u5904\u7406\u540E\u7684\u6570\u636E\u4F20\u9012\u7ED9 <code>GPU</code>\uFF0C<code>GPU</code> \u8FDB\u884C\u6805\u683C\u5316\u6E32\u67D3\u5904\u7406\u3002</li></ol><p>\u4E3A\u4E86\u4FDD\u8BC1\u4EE5\u4E0A\u4E24\u4EF6\u4E8B\u60C5\u5728 <code>16ms</code> \u5185\u5B8C\u6210\uFF0C\u5E94\u8BE5\u5C3D\u91CF\u7F29\u77ED\u5BF9\u8FD9\u4E24\u4EF6\u4E8B\u60C5\u7684\u5904\u7406\u65F6\u95F4\uFF1A</p><ol><li>\u51CF\u5C11\u5E03\u5C40\u63A7\u4EF6\uFF0C\u4F7F <code>CPU</code> \u9700\u8981\u5904\u7406\u7684 <code>UI</code> \u5BF9\u8C61\u51CF\u5C11\uFF0C\u4ECE\u800C\u51CF\u5C11 <code>CPU</code> \u7684\u5904\u7406\u8F6C\u6362\u65F6\u95F4\u3002</li><li>\u907F\u514D\u8FC7\u5EA6\u7ED8\u5236\uFF0C\u4ECE\u800C\u51CF\u5C11 <code>GPU</code> \u7684\u6805\u683C\u5316\u6E32\u67D3\u5904\u7406\u65F6\u95F4\u3002</li></ol><p><img src="'+r+'" alt="" loading="lazy"></p><h2 id="\u8FC7\u5EA6\u7ED8\u5236-overdraw" tabindex="-1"><a class="header-anchor" href="#\u8FC7\u5EA6\u7ED8\u5236-overdraw" aria-hidden="true">#</a> \u8FC7\u5EA6\u7ED8\u5236\uFF08<code>OverDraw</code>\uFF09</h2><h3 id="\u4EC0\u4E48\u662F\u8FC7\u5EA6\u7ED8\u5236" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662F\u8FC7\u5EA6\u7ED8\u5236" aria-hidden="true">#</a> \u4EC0\u4E48\u662F\u8FC7\u5EA6\u7ED8\u5236</h3><p><code>GPU</code> \u7684\u7ED8\u5236\u8FC7\u7A0B\u5C31\u8DDF\u5237\u5899\u4E00\u6837\uFF0C\u4E00\u5C42\u5C42\u5730\u8FDB\u884C\uFF0C<code>16ms</code> \u5237\u4E00\u6B21\uFF0C\u8FD9\u6837\u5C31\u4F1A\u9020\u6210\u56FE\u5C42\u8986\u76D6\u7684\u73B0\u8C61\uFF0C\u5373\u65E0\u7528\u7684\u56FE\u5C42\u8FD8\u4F1A\u88AB\u7ED8\u5236\u5728\u5E95\u5C42\uFF0C\u9020\u6210\u4E0D\u5FC5\u8981\u7684\u6D6A\u8D39\u3002</p><p><img src="'+p+'" alt="" loading="lazy"></p><h3 id="gpu-\u8FC7\u5EA6\u7ED8\u5236\u7684\u51E0\u79CD\u60C5\u51B5" tabindex="-1"><a class="header-anchor" href="#gpu-\u8FC7\u5EA6\u7ED8\u5236\u7684\u51E0\u79CD\u60C5\u51B5" aria-hidden="true">#</a> <code>GPU</code> \u8FC7\u5EA6\u7ED8\u5236\u7684\u51E0\u79CD\u60C5\u51B5</h3><ol><li><p>\u81EA\u5B9A\u4E49\u63A7\u4EF6\u4E2D <code>onDraw</code> \u65B9\u6CD5\u505A\u4E86\u8FC7\u591A\u7684\u91CD\u590D\u7ED8\u5236</p></li><li><p>\u5E03\u5C40\u5C42\u6B21\u592A\u6DF1\uFF0C\u91CD\u53E0\u6027\u592A\u5F3A\u3002\u7528\u6237\u770B\u4E0D\u5230\u7684\u533A\u57DF <code>GPU</code> \u4E5F\u4F1A\u6E32\u67D3\uFF0C\u5BFC\u81F4\u8017\u65F6\u589E\u52A0\u3002</p></li></ol><h3 id="\u8FC7\u5EA6\u7ED8\u5236\u7684\u4F18\u5316-\u4E3B\u8981\u51CF\u5C11-gpu-\u5DE5\u4F5C\u91CF" tabindex="-1"><a class="header-anchor" href="#\u8FC7\u5EA6\u7ED8\u5236\u7684\u4F18\u5316-\u4E3B\u8981\u51CF\u5C11-gpu-\u5DE5\u4F5C\u91CF" aria-hidden="true">#</a> \u8FC7\u5EA6\u7ED8\u5236\u7684\u4F18\u5316\uFF08\u4E3B\u8981\u51CF\u5C11 <code>GPU</code> \u5DE5\u4F5C\u91CF\uFF09</h3><ol><li><p>\u907F\u514D\u5728\u81EA\u5B9A\u4E49\u63A7\u4EF6\u7684 <code>onDraw</code> \u65B9\u6CD5\u4E2D\u5BF9\u540C\u4E00\u533A\u57DF\u8FDB\u884C\u91CD\u590D\u7ED8\u5236\u3002</p><blockquote><p>\u5373\u5728\u81EA\u5B9A\u4E49\u63A7\u4EF6\u4E2D\uFF0C\u5F53\u6709\u591A\u4E2A\u56FE\u5F62\u7684\u90E8\u5206\u533A\u57DF\u91CD\u53E0\u5728\u4E00\u8D77\uFF0C\u4E14\u88AB\u76D6\u5728\u4E0B\u9762\u7684\u56FE\u5F62\u4E0D\u4F1A\u663E\u793A\u51FA\u6765\u65F6\uFF0C\u90A3\u4E48\u5728 <code>onDraw</code> \u65B9\u6CD5\u4E2D\uFF0C\u4E0D\u5E94\u8BE5\u518D\u5BF9\u65E0\u6CD5\u663E\u793A\u51FA\u6765\u7684\u56FE\u5F62\u533A\u57DF\u8FDB\u884C\u7ED8\u5236\u3002\u6B64\u65F6\uFF0C\u5E94\u8BE5\u4ECE\u753B\u5E03\u4E2D\u88C1\u526A\u51FA\u4E00\u5757\u540C\u56FE\u5F62\u7684\u53EF\u663E\u793A\u533A\u57DF\u5927\u5C0F\u4E00\u6837\u7684\u533A\u57DF\uFF0C\u5728\u8FD9\u4E2A\u88C1\u526A\u51FA\u6765\u7684\u533A\u57DF\u4E2D\u7ED8\u5236\u56FE\u5F62\uFF0C\u4ECE\u800C\u56FE\u5F62\u4E2D\u65E0\u6CD5\u663E\u793A\u7684\u90E8\u5206\u5C31\u4E0D\u4F1A\u88AB\u7ED8\u5236\u51FA\u6765\u4E86\u3002</p></blockquote></li><li><p>\u51CF\u5C11\u5E03\u5C40\u6587\u4EF6\u4E2D\u7684\u5D4C\u5957\u5E03\u5C40\u3002</p></li><li><p>\u51CF\u5C11\u80CC\u666F\u91CD\u590D</p><ol><li><p>\u53EF\u4EE5\u8003\u8651\u53BB\u6389 <code>Activity</code> \u4E3B\u9898\u4E2D\u8BBE\u7F6E\u7684\u80CC\u666F\uFF0C\u9700\u8981\u65F6\u81EA\u5DF1\u52A0</p><blockquote><p>\u53BB\u6389\u5355\u4E2A <code>Activity</code> \u7684\u4E3B\u9898\u8BBE\u7F6E\u7684\u80CC\u666F\u5C5E\u6027\uFF1A\u5728 <code>setContentView</code> \u4E4B\u524D\u8C03\u7528 <code>getWindow().setBackgroundDrawable(null)</code></p><p>\u53BB\u6389\u6240\u6709 <code>Activity</code> \u4E3B\u9898\u4E2D\u8BBE\u7F6E\u7684\u80CC\u666F\u5C5E\u6027\uFF1A\u5728 <code>styles.xml</code> \u4E2D\u8BBE\u7F6E <code>&lt;item name=&quot;android:windowBackground&quot;&gt;@null&lt;/item&gt;</code></p></blockquote></li><li><p>\u975E\u4E1A\u52A1\u9700\u8981\uFF0C\u4E0D\u8981\u53BB\u8BBE\u7F6E\u80CC\u666F\u3002</p></li><li><p>\u5C06\u4E0D\u80FD\u53BB\u6389\uFF0C\u4F46\u53C8\u4E0D\u4F1A\u663E\u793A\u51FA\u6765\u7684\u80CC\u666F\u8BBE\u7F6E\u4E3A\u900F\u660E\u3002</p><blockquote><p>\u5C06\u80CC\u666F\u8BBE\u7F6E\u4E3A\u900F\u660E\u65F6\uFF0C\u53EA\u662F <code>CPU</code> \u4F1A\u53BB\u8BA1\u7B97\u900F\u660E\u80CC\u666F\u7684\u5C3A\u5BF8\uFF0C\u4F4D\u7F6E\u7B49\u4FE1\u606F\uFF0C\u4F46\u662F\u5E76\u4E0D\u4F1A\u518D\u4F20\u9012\u7ED9 <code>GPU</code> \u53BB\u5904\u7406\u3002</p></blockquote></li></ol></li><li><p><code>Android 7.0</code> \u4E4B\u540E\u7CFB\u7EDF\u505A\u51FA\u7684\u4F18\u5316\uFF1A<code>invalidate()</code> \u65B9\u6CD5\u4E0D\u518D\u6267\u884C\u6D4B\u91CF\u548C\u5E03\u5C40\u52A8\u4F5C\u3002</p></li></ol><h3 id="\u5982\u4F55\u67E5\u770B\u8FC7\u5EA6\u7ED8\u5236" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u67E5\u770B\u8FC7\u5EA6\u7ED8\u5236" aria-hidden="true">#</a> \u5982\u4F55\u67E5\u770B\u8FC7\u5EA6\u7ED8\u5236</h3><p>\u5728\u624B\u673A\u7AEF\u7684\u5F00\u53D1\u8005\u9009\u9879\u91CC\uFF0C\u5F00\u542F \u201C\u8C03\u7528 <code>GPU</code> \u8FC7\u5EA6\u7ED8\u5236\u201D</p><p><img src="'+l+'" alt="" loading="lazy"><img src="'+t+'" alt="" loading="lazy"></p><p>\u5982\u4E0A\u56FE\uFF0C\u5176\u4E2D\u989C\u8272\u4EE3\u8868\u6E32\u67D3\u7684\u56FE\u5C42\u60C5\u51B5\uFF1A</p><ol><li>\u754C\u9762\u4E0A\u4EC0\u4E48\u90FD\u6CA1\u6709\u7ED8\u5236\u65F6\uFF0C\u65E0\u56FE\u5C42\uFF0C\u6B64\u65F6\u8BE5\u533A\u57DF\u663E\u793A\u767D\u8272\uFF1B</li><li>\u754C\u9762\u4E0A\u7ED8\u5236\u4E86 <code>1</code> \u6B21\u7684\u533A\u57DF\uFF0C\u6709 <code>1</code> \u5C42\u56FE\u5C42\uFF0C\u6B64\u65F6\u8BE5\u533A\u57DF\u663E\u793A\u84DD\u8272\uFF08\u6DE1\u7D2B\u8272\uFF09\uFF1B</li><li>\u754C\u9762\u4E0A\u7ED8\u5236\u4E86 <code>2</code> \u6B21\u7684\u533A\u57DF\uFF0C\u6709 <code>2</code> \u5C42\u56FE\u5C42\uFF0C\u6B64\u65F6\u8BE5\u533A\u57DF\u663E\u793A\u6D45\u7EFF\u8272\uFF1B</li><li>\u754C\u9762\u4E0A\u7ED8\u5236\u4E86 <code>3</code> \u6B21\u7684\u533A\u57DF\uFF0C\u6709 <code>3</code> \u5C42\u56FE\u5C42\uFF0C\u6B64\u65F6\u8BE5\u533A\u57DF\u663E\u793A\u6D45\u7EA2\u8272\uFF08\u7C89\u7EA2\u8272\uFF09\uFF1B</li><li>\u754C\u9762\u4E0A\u7ED8\u5236\u4E86 <code>4</code> \u6B21\u7684\u533A\u57DF\uFF0C\u6709 <code>4</code> \u5C42\u56FE\u5C42\uFF0C\u6B64\u65F6\u8BE5\u533A\u57DF\u663E\u793A\u6DF1\u7EA2\u8272\uFF08\u7EA2\u8272\uFF09\uFF1B</li></ol><blockquote><p>\u754C\u9762\u4E0A\u7ED8\u5236\u4E86 <code>4</code> \u6B21\u4EE5\u4E0A\u7684\u533A\u57DF\uFF0C\u90FD\u663E\u793A\u4E3A\u6DF1\u7EA2\u8272\uFF08\u7EA2\u8272\uFF09\u3002</p><p>\u6211\u4EEC\u7684\u76EE\u6807\u5C31\u662F\u5C3D\u91CF\u51CF\u5C11\u7EA2\u8272 <code>OverDraw</code>\uFF0C\u770B\u5230\u66F4\u591A\u7684\u84DD\u8272\u533A\u57DF\u3002</p></blockquote><h2 id="\u5E03\u5C40\u7684\u4F18\u5316-\u4E3B\u8981\u51CF\u5C11-cpu-\u5DE5\u4F5C\u91CF" tabindex="-1"><a class="header-anchor" href="#\u5E03\u5C40\u7684\u4F18\u5316-\u4E3B\u8981\u51CF\u5C11-cpu-\u5DE5\u4F5C\u91CF" aria-hidden="true">#</a> \u5E03\u5C40\u7684\u4F18\u5316\uFF08\u4E3B\u8981\u51CF\u5C11 <code>CPU</code> \u5DE5\u4F5C\u91CF\uFF09</h2><h3 id="\u5982\u4F55\u67E5\u770B\u5E03\u5C40\u7684\u89C6\u56FE\u5C42\u7EA7-hierarchy-view" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u67E5\u770B\u5E03\u5C40\u7684\u89C6\u56FE\u5C42\u7EA7-hierarchy-view" aria-hidden="true">#</a> \u5982\u4F55\u67E5\u770B\u5E03\u5C40\u7684\u89C6\u56FE\u5C42\u7EA7\uFF08<code>Hierarchy View</code>\uFF09</h3><p><strong>\u5E38\u7528\u5DE5\u5177\uFF1A</strong></p><ol><li><p><code>Android Device Monitor</code> \u4E2D\u7684 <code>Hierarchy View</code> \u7A97\u53E3\u4E0B\u7684 <code>TreeView</code> \u5DE5\u5177</p><blockquote><p>\u51FA\u4E8E\u5B89\u5168\u8003\u8651 <code>Hierarchy Viewer</code> \u53EA\u80FD\u8FDE\u63A5 <code>Android</code> \u5F00\u53D1\u7248\u624B\u673A\u6216\u8005\u6A21\u62DF\u5668\uFF08\u51C6\u786E\u5730\u8BF4\uFF0C\u53EA\u6709\u53C2\u6570 <code>ro.secure</code> \u8BBE\u7F6E\u4E3A <code>0</code>\uFF0C\u4E14 <code>ro.debuggable</code> \u8BBE\u7F6E\u4E3A <code>1</code> \u7684 <code>Android</code> \u7CFB\u7EDF\uFF09\u3002</p><p><code>Hierarchy Viewer</code> \u5728\u8FDE\u63A5\u624B\u673A\u65F6\uFF0C\u624B\u673A\u4E0A\u5FC5\u987B\u542F\u52A8\u4E00\u4E2A\u53EB <code>View Server</code> \u7684\u5BA2\u6237\u7AEF\uFF0C\u7528\u6765\u4E0E <code>Hierarchy Viewer</code> \u8FDB\u884C <code>socket</code> \u901A\u4FE1\u3002\u800C\u5728\u5546\u4E1A\u624B\u673A\u4E0A\uFF0C\u662F\u65E0\u6CD5\u5F00\u542F <code>View Server</code> \u7684\uFF0C\u6545 <code>Hierarchy Viewer</code> \u662F\u65E0\u6CD5\u8FDE\u63A5\u5230\u666E\u901A\u7684\u5546\u4E1A\u624B\u673A\u7684\u3002</p><p>\u8FD9\u91CC\u7684 <code>Hierarchy Viewer</code> \u6307\u7684\u5C31\u662F <code>TreeView</code> \u3002</p></blockquote></li><li><p><code>AndroidStudio</code> \u81EA\u5E26\u7684 <code>Layout Inspector</code> \u5DE5\u5177</p><blockquote><p><code>Layout Inspector</code> \u65E0\u6CD5\u7528\u6765\u67E5\u770B\u5E03\u5C40\u6027\u80FD\uFF0C\u53EA\u80FD\u67E5\u770B\u5E03\u5C40\u5C42\u7EA7\u3002</p><p><code>Live Layout Inspecotr</code> \u7684\u529F\u80FD\u9700\u8981 <code>API level 29</code> \u4EE5\u4E0A\u624D\u652F\u6301</p></blockquote></li></ol><h3 id="\u901A\u8FC7-treeview-\u67E5\u770B\u89C6\u56FE\u5C42\u7EA7-hierarchy-view" tabindex="-1"><a class="header-anchor" href="#\u901A\u8FC7-treeview-\u67E5\u770B\u89C6\u56FE\u5C42\u7EA7-hierarchy-view" aria-hidden="true">#</a> \u901A\u8FC7 <code>TreeView</code> \u67E5\u770B\u89C6\u56FE\u5C42\u7EA7\uFF08<code>Hierarchy View</code>\uFF09</h3><p><img src="'+s+'" alt="" loading="lazy"></p><p><img src="'+n+'" alt="" loading="lazy"></p><p><img src="'+h+'" alt="" loading="lazy"></p><p><img src="'+g+'" alt="" loading="lazy"></p><p><img src="'+u+'" alt="" loading="lazy"></p><h3 id="\u5E03\u5C40\u7684\u4F18\u5316\u601D\u8DEF" tabindex="-1"><a class="header-anchor" href="#\u5E03\u5C40\u7684\u4F18\u5316\u601D\u8DEF" aria-hidden="true">#</a> \u5E03\u5C40\u7684\u4F18\u5316\u601D\u8DEF</h3><ol><li><p>\u5C3D\u53EF\u80FD\u628A\u76F8\u540C\u7684\u5BB9\u5668\u5408\u5E76 <code>merge</code></p></li><li><p>\u80FD\u5728\u4E00\u4E2A\u5E73\u9762\u663E\u793A\u7684\u5185\u5BB9\uFF0C\u5C3D\u91CF\u53EA\u7528\u4E00\u4E2A\u5BB9\u5668\uFF08\u51CF\u5C11\u5E03\u5C40\u5D4C\u5957\uFF09</p></li><li><p>\u80FD\u590D\u7528\u7684\u4EE3\u7801\uFF0C\u7528 <code>include</code> \u5904\u7406\uFF0C\u53EF\u4EE5\u51CF\u5C11 <code>GPU</code> \u91CD\u590D\u5DE5\u4F5C</p></li></ol><h4 id="merge-\u6807\u7B7E\u7684\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#merge-\u6807\u7B7E\u7684\u4F5C\u7528" aria-hidden="true">#</a> <code>merge</code> \u6807\u7B7E\u7684\u4F5C\u7528</h4><p><img src="'+m+'" alt="" loading="lazy"></p><h4 id="\u4F7F\u7528-constraintlayout-\u6216-relativelayout-\u51CF\u5C11\u5E03\u5C40\u5D4C\u5957" tabindex="-1"><a class="header-anchor" href="#\u4F7F\u7528-constraintlayout-\u6216-relativelayout-\u51CF\u5C11\u5E03\u5C40\u5D4C\u5957" aria-hidden="true">#</a> \u4F7F\u7528 <code>ConstraintLayout</code> \u6216 <code>RelativeLayout</code> \u51CF\u5C11\u5E03\u5C40\u5D4C\u5957</h4><p><img src="'+y+'" alt="" loading="lazy"></p><h4 id="include-\u6807\u7B7E\u7684\u4F5C\u7528" tabindex="-1"><a class="header-anchor" href="#include-\u6807\u7B7E\u7684\u4F5C\u7528" aria-hidden="true">#</a> <code>include</code> \u6807\u7B7E\u7684\u4F5C\u7528</h4><p>\u5982\u679C\u591A\u4E2A\u754C\u9762\u4E2D\u5B58\u5728\u76F8\u540C\u7684\u5E03\u5C40\uFF08\u5982\u5217\u8868\u7684\u5934\u90E8\u5E03\u5C40\u3001\u5E95\u90E8\u5E03\u5C40\uFF0C\u6216\u7F51\u7EDC\u52A0\u8F7D\u5931\u8D25\u5E03\u5C40\uFF09\uFF0C \u90A3\u4E48\u5E94\u8BE5\u628A\u8FD9\u79CD\u76F8\u540C\u7684\u5E03\u5C40\u5355\u72EC\u5730\u5199\u5728\u4E00\u4E2A <code>xml</code> \u6587\u4EF6\u4E2D\uFF0C\u7136\u540E\u5728\u9700\u8981\u4F7F\u7528\u5230\u7684\u5730\u65B9\u901A\u8FC7 <code>include</code> \u6807\u7B7E\u5F15\u8FDB\u3002</p><p>\u8FD9\u6837\u505A\u7684\u597D\u5904\u5C31\u662F\uFF0C<code>CPU</code> \u53EA\u9700\u8981\u5BF9 <code>include</code> \u5F15\u5165\u7684\u5E03\u5C40\u89C6\u56FE\u8BA1\u7B97\u4E00\u6B21\uFF0C\u5C06\u76F8\u5E94\u7684\u7ED8\u5236\u6570\u636E\u4F20\u9012\u7ED9 <code>GPU</code> \u540E\uFF0C<code>GPU</code> \u4F1A\u8FDB\u884C\u7F13\u5B58\u3002\u4E8E\u662F\uFF0C\u5F53\u4E0B\u4E00\u6B21\u518D\u4F7F\u7528 <code>include</code> \u5F15\u5165\u76F8\u540C\u7684\u5E03\u5C40\u65F6\uFF0C<code>CPU</code> \u5C31\u4E0D\u4F1A\u518D\u8BA1\u7B97\u4E86\uFF0C\u5E76\u4E14 <code>GPU</code> \u4E5F\u4F1A\u76F4\u63A5\u4ECE\u7F13\u5B58\u4E2D\u83B7\u53D6\u76F8\u5E94\u7684\u7ED8\u56FE\u6570\u636E\u5BF9\u5176\u8FDB\u884C\u6E32\u67D3\u3002</p>',61);function v(U,f){return _}var w=e(b,[["render",v],["__file","layout-optimize.html.vue"]]);export{w as default};
